/**
 * External dependencies
 */
import PropTypes from 'prop-types';
import { createElement } from '@wordpress/element';
/**
 * A component to display summarized table data - the list of data passed in on a single line.
 *
 * @param {Object} props
 * @param {Array}  props.data
 * @return {Object} -
 */
const TableSummary = ({ data }) => {
    return (createElement("ul", { className: "woocommerce-table__summary", role: "complementary" }, data.map(({ label, value }, i) => (createElement("li", { className: "woocommerce-table__summary-item", key: i },
        createElement("span", { className: "woocommerce-table__summary-value" }, value),
        createElement("span", { className: "woocommerce-table__summary-label" }, label))))));
};
TableSummary.propTypes = {
    /**
     * An array of objects with `label` & `value` properties, which display on a single line.
     */
    data: PropTypes.array,
};
export default TableSummary;
/**
 * A component to display a placeholder box for `TableSummary`. There is no prop for this component.
 *
 * @return {Object} -
 */
export const TableSummaryPlaceholder = () => {
    return (createElement("ul", { className: "woocommerce-table__summary is-loading", role: "complementary" },
        createElement("li", { className: "woocommerce-table__summary-item" },
            createElement("span", { className: "is-placeholder" }))));
};
