/**
 * External dependencies
 */
import { registerStore } from '@wordpress/data';
/**
 * Internal dependencies
 */
import { createSelectors } from './selectors';
import { createDispatchActions } from './actions';
import controls from '../controls';
import { createResolvers } from './resolvers';
import { createReducer } from './reducer';
export const createCrudDataStore = ({ storeName, resourceName, namespace, pluralResourceName, }) => {
    const reducer = createReducer();
    const actions = createDispatchActions({
        resourceName,
        namespace,
    });
    const resolvers = createResolvers({
        resourceName,
        pluralResourceName,
        namespace,
    });
    const selectors = createSelectors({ resourceName, pluralResourceName });
    registerStore(storeName, {
        reducer: reducer,
        actions,
        selectors,
        resolvers,
        controls,
    });
};
